mapper.update:
update Student_select_subject
set grade_selfjudge = #{0}
where user_id = #{1}
  and subject_id = #{2};

update Check_in
set today_checked_in = 1
where user_id = #{userid};

mapper.select:
select today_checked_in
from Check_in
where user_id = #{userid};

select student_sex, student_class
from Students
where user_id = #{userid};
