mapper.select:
select max_select_num
from Subjects
where subject_id = #{subjectId};

select curr_select_num
from Subjects
where subject_id = #{subjectId};

mapper.update:
update Subjects
set subject_name    = #{subjectName},
    subject_kind    = #{subjectKind},
    max_select_num  = #{maxSelectNum},
    curr_select_num = #{currSelectNum}
where subject_id = #{subjectId};
